// Add a function to get all cities for autocomplete, if needed later
export const getAllCities = () => {
    let allCities = [];
    for (const county in hungarianCounties) {
        if (county !== "Budapest") {
            allCities = allCities.concat(hungarianCounties[county]);
        }
    }
    return [...new Set(allCities)]; // Return unique city names
};